mapper.select:
SELECT * FROM orders WHERE 1 = 1
AND total_amount >= #{form.minAmount}
AND total_amount <= #{form.maxAmount}
AND customer_name LIKE CONCAT('%', #{form.customerName}, '%')
AND customer_address LIKE CONCAT('%', #{form.customerAddress}, '%')
AND customer_phone LIKE CONCAT('%', #{form.customerPhone}, '%')
AND status = #{form.status}
AND created_at >= #{form.startTimeStamp}
AND created_at < DATE_ADD(#{form.endTimeStamp}, INTERVAL 1 DAY)
ORDER BY
id user_id total_amount customer_name customer_address created_at id
DESC ASC
LIMIT #{limit} OFFSET #{offset}

