mapper.select:
SELECT *
FROM products
WHERE is_active = TRUE


    AND category = #{category}



    AND name LIKE CONCAT('%', #{keyword}, '%')



    AND price >= #{minPrice}



    AND price <= #{maxPrice}



    AND stock >= #{stock}




        ORDER BY created_at DESC


        ORDER BY price ASC


        ORDER BY price DESC


        ORDER BY updated_at DESC



LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM products
WHERE is_active = TRUE


    AND category = #{category}



    AND name LIKE CONCAT('%', #{keyword}, '%')



    AND price >= #{minPrice}



    AND price <= #{maxPrice}



    AND stock >= #{stock}

SELECT
id, name, price, stock, category, image_url,
is_active, created_at
FROM products
WHERE 1 = 1



    AND name LIKE CONCAT('%', #{cond.name}, '%')




    AND price >= #{cond.priceMin}


    AND price <= #{cond.priceMax}




    AND category = #{cond.category}




    AND stock > 0




    AND is_active = #{cond.isActive}




    AND DATE(created_at) >= #{cond.dateFrom}




    AND DATE(created_at) <= #{cond.dateTo}





        ORDER BY ${cond.sortKey}

            ASC
            DESC



        ORDER BY id ASC




LIMIT #{cond.size}
OFFSET #{cond.offset}

SELECT COUNT(*) FROM products
WHERE 1 = 1


    AND name LIKE CONCAT('%', #{cond.name}, '%')


    AND price >= #{cond.priceMin}


    AND price <= #{cond.priceMax}


    AND category = #{cond.category}


    AND stock > 0


    AND is_active = #{cond.isActive}


    AND DATE(created_at) >= #{cond.dateFrom}


    AND DATE(created_at) <= #{cond.dateTo}

SELECT
id, name, price, stock, category, image_url,
is_active, created_at
FROM products
WHERE 1 = 1



    AND name LIKE CONCAT('%', #{cond.name}, '%')




    AND price >= #{cond.priceMin}


    AND price <= #{cond.priceMax}




    AND category = #{cond.category}




    AND stock > 0




    AND is_active = #{cond.isActive}




    AND DATE(created_at) >= #{cond.dateFrom}




    AND DATE(created_at) <= #{cond.dateTo}





        ORDER BY ${cond.sortKey}

            ASC
            DESC



        ORDER BY id ASC

mapper.update:
UPDATE products
SET stock = #{stock}
WHERE id = #{id}
